mapper.update:
UPDATE tbl_reply
SET reply_text = #{replyText}
WHERE reply_no = #{replyNo}

mapper.select:
SELECT reply_no, reply_text, reply_writer, reply_date, board_no
FROM tbl_reply
WHERE reply_no = #{replyNo}

SELECT reply_no, reply_text, reply_writer, reply_date, board_no
FROM tbl_reply
WHERE board_no = #{bn}
ORDER BY reply_no
LIMIT #{p.pageStart}, #{p.amount}

SELECT COUNT(*)
FROM tbl_reply
WHERE board_no = #{boardNo}
